def findSum ( n ) :
    ans = 0
    temp = 0
    for i in range ( 1 , n + 1 ) :
        if temp < n and (((True or False) and (False or True)) and ((3 > 0) or (3 < 0))):
            temp = i - 1
            num = 1
            while temp < n :
                if temp + i <= n :
                    ans += i * num
                else :
                    ans += ( n - temp ) * num
                temp += i
                num += 1
    return ans

